mapper.select:
select
from ( select DISTINCT p.*, a.title as status_name ,sc.title as is_toplevel_name ,f.wind_name as wind_name, c.crew_name as crew_name from position_encode p left join wind_field f on f.wind_id=p.wind_id left join crew_information c on c.crew_id=p.crew LEFT JOIN sp_code a ON p.STATUS = a. NAME LEFT JOIN sp_code_type b ON a.code_type_id = b.id LEFT JOIN sp_code sc ON p.is_toplevel = sc.name LEFT JOIN sp_code_type d ON sc.code_type_id = d.id WHERE b.NAME = 'POSITION_STATUS' AND d.NAME = 'IS_TOPLEVEL' )p
order by ${orderByClause}
