mapper.update:
update breakdown_info set status = "0"
where breakdown_id in

  #{item}

mapper.select:
select

from breakdown_info a
left join breakdown_dealwith_mode b on a.mode_id=b.mode_id
left join wind_field c on a.wind_id = c.wind_id

left join position_encode e on a.position_id = e.position_id
left join crew_information f on a.crew_id = f.crew_id

left join breakdown_location h on a.location_id = h.location_id
left join breakdown_attribute i on a.attribute_id = i.attribute_id
where a.status  <>  '0'

  and a.wind_id = #{map.windId}


  and a.crew_id = #{map.crewId}


  and a.breakdownDesc like '%${map.breakdownDesc}%'


  and a.breakdownCode = #{map.breakdownCode}


  and a.mode_id = #{map.modeId}


  and a.location_id = #{map.locationId}


  and a.attribute_id = #{map.attributeId}


  and a.active_time >= #{map.startTime}


  and a.active_time <= #{map.endTime}

order by a.active_time asc
limit ${map.start},${map.pageSize}

select
count(*)
from breakdown_info
where status  <>  '0'

  and wind_id = #{map.windId}


  and crew_id = #{map.crewId}


  and breakdownDesc like '%${map.breakdownDesc}%'


  and breakdownCode = #{map.breakdownCode}


  and mode_id = #{map.modeId}


  and location_id = #{map.locationId}


  and attribute_id = #{map.attributeId}


  and active_time >= #{map.startTime}


  and active_time <= #{map.endtime}
