mapper.update:
update breakdown_info set status = "0"
where breakdown_id in

  #{item}

mapper.select:
select

from breakdown_info a
left join org_architecture b on a.wind_id = b.architecture_id and architecture_type = '02'
left join crew_information d on a.crew_id = d.crew_id
left join position_encode e on a.position_id = e.position_id
left join equipment_asset_ledger f on a.equipment_id = f.equipment_id
left join sp_code g on a.breakdown_location =g.id
left join sp_code h on a.breakdown_attribute =h.id
left join sp_code i on a.dealwith_mode =i.id
where a.status  <>  '0'

  and a.wind_id = #{map.windId}


  and a.crew_id = #{map.crewId}


  and a.breakdown_desc like '%${map.breakdownDesc}%'


  and a.breakdown_code = #{map.breakdownCode}


  and a.dealwith_mode = #{map.dealwithMode}


  and a.dealwith_location = #{map.dealwithLocation}


  and a.dealwith_attribute = #{map.dealwithAttribute}


  and a.active_time >= #{map.startTime}


  and a.active_time <= #{map.endtime}

order by a.active_time asc
limit ${map.start},${map.pageSize}

select
count(*)
from breakdown_info
where status  <>  '0'

  and wind_id = #{map.windId}


  and crew_id = #{map.crewId}


  and breakdown_desc like '%${map.breakdownDesc}%'


  and breakdown_code = #{map.breakdownCode}


  and dealwith_mode = #{map.dealwithMode}


  and dealwith_location = #{map.dealwithLocation}


  and dealwith_attribute = #{map.dealwithAttribute}


  and active_time >= #{map.startTime}


  and active_time <= #{map.endtime}

    select count(*)  from  breakdown_info i 
	LEFT JOIN breakdown_location l ON i.breakdown_location = l.location_id  


          and i.active_time between  #{activeTime} and  #{activeTime2}

SELECT CASE WHEN h.wind_id IS NOT NULL then h.wind_id else "合计" end wind_id, SUM(A) A, SUM(B) B, SUM(C) C, SUM(D) D, SUM(E) E,SUM(F) F,SUM(G) G,f.wind_name from (SELECT i.wind_id, count( CASE WHEN i.breakdown_code =1001 THEN breakdown_code END ) as A, count( CASE WHEN i.breakdown_code =1002 THEN breakdown_code END ) as B, count( CASE WHEN i.breakdown_code =1003 THEN breakdown_code END ) as C, count( CASE WHEN i.breakdown_code =1004 THEN breakdown_code END ) as D, count( CASE WHEN i.breakdown_code =1005 THEN breakdown_code END ) as E, count( CASE WHEN i.breakdown_code =1006 THEN breakdown_code END ) as F, count( CASE WHEN i.breakdown_code =1007 THEN breakdown_code END ) as G FROM breakdown_info i LEFT JOIN breakdown_location l ON i.breakdown_location = l.location_id
and i.active_time between #{activeTime} and #{activeTime2}
group by i.wind_id) h LEFT JOIN wind_field f ON f.wind_id = h.wind_id GROUP by h.wind_id with rollup

    select count(*)  from  breakdown_info i 
	LEFT JOIN breakdown_attribute a ON i.breakdown_attribute = a.attribute_id   


          and i.active_time between  #{activeTime} and  #{activeTime2}

SELECT CASE WHEN a.wind_id IS NOT NULL then a.wind_id else "合计" end wind_id, SUM(A) A, SUM(B) B, SUM(C) C ,concat (left ( sum(a.B)/ sum(a.A) *100,5),'%')as D from (SELECT h.*, ( Sum( h.A ) - Sum( h.B ) ) AS C ,concat (left ( SUM(h.B)/ sum(h.A) *100,5),'%')as D FROM ( SELECT i.wind_id, count( CASE WHEN i.active_time IS NOT NULL THEN active_time END ) AS A, count( CASE WHEN i.recover_time IS NOT NULL THEN recover_time END ) AS B FROM breakdown_info i
and i.active_time between #{activeTime} and #{activeTime2}
GROUP BY i.wind_id ) h group by h.wind_id ) a GROUP BY a.wind_id with rollup

SELECT CASE WHEN h.wind_id IS NOT NULL THEN h.wind_id ELSE "合计" END wind_id, SUM( A ) A, SUM( B ) B, SUM( C ) C, SUM( D ) D, SUM( E ) E, SUM( F ) F, SUM( G ) G, SUM( H ) H, SUM( I ) I, SUM( J ) J, SUM( K ) K, SUM( L ) L, SUM( M ) M, f.wind_name FROM ( SELECT i.wind_id, count( CASE WHEN i.breakdown_location = 1001 THEN breakdown_location END ) AS A, count( CASE WHEN i.breakdown_location = 1002 THEN breakdown_location END ) AS B, count( CASE WHEN i.breakdown_location = 1003 THEN breakdown_location END ) AS C, count( CASE WHEN i.breakdown_location = 1004 THEN breakdown_location END ) AS D, count( CASE WHEN i.breakdown_location = 1005 THEN breakdown_location END ) AS E, count( CASE WHEN i.breakdown_location = 1006 THEN breakdown_location END ) AS F, count( CASE WHEN i.breakdown_location = 1007 THEN breakdown_location END ) AS G, count( CASE WHEN i.breakdown_attribute = 1001 THEN breakdown_attribute END ) AS H, count( CASE WHEN i.breakdown_attribute = 1002 THEN breakdown_attribute END ) AS I, count( CASE WHEN i.breakdown_attribute = 1003 THEN breakdown_attribute END ) AS J, count( CASE WHEN i.breakdown_attribute = 1004 THEN breakdown_attribute END ) AS K, count( CASE WHEN i.breakdown_attribute = 1005 THEN breakdown_attribute END ) AS L, count( CASE WHEN i.breakdown_attribute = 1006 THEN breakdown_attribute END ) AS M FROM breakdown_info i LEFT JOIN breakdown_location l ON i.breakdown_location = l.location_id LEFT JOIN breakdown_attribute a on i.breakdown_attribute=a.attribute_id
and i.active_time between #{activeTime} and #{activeTime2}
and i.wind_id=#{windId}
GROUP BY i.wind_id ) h LEFT JOIN wind_field f ON f.wind_id = h.wind_id GROUP BY h.wind_id WITH ROLLUP
