mapper.update:
update operate_ticket set status = "0"
where ticket_id in

  #{item}

mapper.select:
select

from operate_ticket a
left join org_architecture b on a.wind_id = b.architecture_id and architecture_type = '02'
left join crew_information d on a.crew_id = d.crew_id
left join position_encode e on a.position_id = e.position_id
left join equipment_asset_ledger f on a.equipment_id = f.equipment_id
left join operate_ticket_type g on a.oper_ticket_type_id = g.oper_ticket_type_id
left join typical_operate_ticket h on a.typical_ticket_id = h.typical_ticket_id
where a.status != '0'

  and a.oper_ticket_type_id = #{map.operTicketTypeId}


  and a.wind_id = #{map.windId}


  and a.status = #{map.status}


  and a.operate_content like '%${map.operateContent}%'


  and a.apply_time >= #{map.startTime}


  and a.apply_time <= #{map.endTime}

order by a.serial_number desc
limit ${map.start},${map.pageSize};

select
count(*)
from operate_ticket
where status != '0'

  and oper_ticket_type_id = #{map.operTicketTypeId}


  and wind_id = #{map.windId}


  and status = #{map.status}


  and operate_content like '%${map.operateContent}%'


  and apply_time >= #{map.startTime}


  and apply_time <= #{map.endTime}

select a.*,(a.complete_count/a.apply_count) as 'complete_rate'
from (select wind_id,count(*) as 'apply_count',
sum(IF(status = '1', 1, 0)) as 'in_process_count',
sum(IF(status = '2', 1, 0)) as 'complete_count',
sum(IF(status = '0', 1, 0)) as 'invalid_count'
from operate_ticket
where 1=1

  and apply_time >= #{startTime}


  and apply_time <= #{endTime}

group by wind_id) as a;
