mapper.delete:
delete from work_ticket
where ticket_id in

  #{item}

mapper.update:
update work_ticket set status = '0'
where ticket_id in

  #{item}

update work_ticket set workorder_id = null
where workorder_id in

  #{item}

update work_ticket set order_id = null
where order_id in

  #{item}

mapper.select:
select distinct
from work_ticket a left join org_architecture b on (a.wind_id = b.architecture_code and b.architecture_type = '02') left join crew_information d on a.crew_id = d.crew_id left join position_encode e on a.position_id = e.position_id left join equipment_asset_ledger f on a.equipment_id = f.equipment_asset_id left join work_ticket_type g on a.work_ticket_type_id = g.work_ticket_type_id left join standard_work_ticket h on a.standard_ticket_id = h.standard_ticket_id left join sp_code i on (a.flow_status = i.name and i.code_type_id = '1046') left join org_architecture j on a.work_team = j.architecture_id left join isc_user ua on a.maintainer = ua.user_id left join isc_user ub on a.applicant = ub.user_id left join isc_user uc on a.work_head = uc.user_id left join isc_user ud on a.signer = ud.user_id left join isc_user ue on a.run_main_person = ue.user_id left join isc_user uf on a.receive_head = uf.user_id left join isc_user ug on a.scene_licensor = ug.user_id left join isc_user uh on a.run_licensor = uh.user_id left join isc_user ui on a.head_change_to = ui.user_id left join isc_user uj on a.head_change_signer = uj.user_id left join isc_user uk on a.delay_head = uk.user_id left join isc_user ul on a.delay_licensor = ul.user_id left join isc_user um on a.work_end_head = um.user_id left join isc_user un on a.work_end_licensor = un.user_id left join isc_user uo on a.ticket_end_licensor = uo.user_id left join isc_user up on a.guardian = up.user_id left join isc_user uq on a.evaluation = uq.user_id left join process k on (a.ticket_id = k.order_id and k.process_status = '01') left join process_detail l on (k.process_id = l.process_id and ((k.process_sort+1) = l.sort)) left join process_detail_role_rela m on l.process_detail_id = m.process_detail_id left join approve_record n on a.ticket_id = n.order_id left join isc_user ur on n.approve_user = ur.user_id where 1=1
and a.work_ticket_type_id = #{map.workTicketTypeId}
and a.wind_id = #{map.startWindId}
and a.wind_id BETWEEN #{map.startWindId} and #{map.endWindId}
and ((a.status = '01' or a.status = '02' or a.status = '04') or (a.status = '03' and a.maintainer = #{userId}))
and a.status = #{map.status}
and (a.status = #{map.status} and a.maintainer = #{userId})
and (((a.flow_status = '01') and (m.role_id in
#{item.roleId}
or ur.user_name = #{map.approveUser})) or ((a.flow_status = '02' or a.flow_status = '03') and ur.user_name = #{map.approveUser}))
and a.crew_id = #{map.crewId}
and a.work_content like '%${map.workContent}%'
and a.apply_time between #{map.startTime}
and (a.apply_time between #{map.startTime} and #{map.endTime})
order by a.serial_number is null desc,a.serial_number desc,a.maintain_date desc limit ${map.start},${map.pageSize}

select count(distinct a.ticket_id) from work_ticket a left join org_architecture b on (a.wind_id = b.architecture_code and b.architecture_type = '02') left join crew_information d on a.crew_id = d.crew_id left join position_encode e on a.position_id = e.position_id left join equipment_asset_ledger f on a.equipment_id = f.equipment_asset_id left join work_ticket_type g on a.work_ticket_type_id = g.work_ticket_type_id left join standard_work_ticket h on a.standard_ticket_id = h.standard_ticket_id left join sp_code i on (a.flow_status = i.name and i.code_type_id = '1046') left join org_architecture j on a.work_team = j.architecture_id left join isc_user ua on a.maintainer = ua.user_id left join isc_user ub on a.applicant = ub.user_id left join isc_user uc on a.work_head = uc.user_id left join isc_user ud on a.signer = ud.user_id left join isc_user ue on a.run_main_person = ue.user_id left join isc_user uf on a.receive_head = uf.user_id left join isc_user ug on a.scene_licensor = ug.user_id left join isc_user uh on a.run_licensor = uh.user_id left join isc_user ui on a.head_change_to = ui.user_id left join isc_user uj on a.head_change_signer = uj.user_id left join isc_user uk on a.delay_head = uk.user_id left join isc_user ul on a.delay_licensor = ul.user_id left join isc_user um on a.work_end_head = um.user_id left join isc_user un on a.work_end_licensor = un.user_id left join isc_user uo on a.ticket_end_licensor = uo.user_id left join isc_user up on a.guardian = up.user_id left join isc_user uq on a.evaluation = uq.user_id left join process k on (a.ticket_id = k.order_id and k.process_status = '01') left join process_detail l on (k.process_id = l.process_id and ((k.process_sort+1) = l.sort)) left join process_detail_role_rela m on l.process_detail_id = m.process_detail_id left join approve_record n on a.ticket_id = n.order_id left join isc_user ur on n.approve_user = ur.user_id where 1=1
and a.work_ticket_type_id = #{map.workTicketTypeId}
and a.wind_id = #{map.startWindId}
and a.wind_id BETWEEN #{map.startWindId} and #{map.endWindId}
and ((a.status = '01' or a.status = '02' or a.status = '04') or (a.status = '03' and a.maintainer = #{userId}))
and a.status = #{map.status}
and (a.status = #{map.status} and a.maintainer = #{userId})
and (((a.flow_status = '01') and (m.role_id in
#{item.roleId}
or ur.user_name = #{map.approveUser})) or ((a.flow_status = '02' or a.flow_status = '03') and ur.user_name = #{map.approveUser}))
and a.crew_id = #{map.crewId}
and a.work_content like '%${map.workContent}%'
and a.apply_time between #{map.startTime}
and a.apply_time between #{map.startTime} and #{map.endTime}

select
from work_ticket a left join org_architecture b on a.wind_id = b.architecture_code and b.architecture_type = '02' left join crew_information d on a.crew_id = d.crew_id left join position_encode e on a.position_id = e.position_id left join equipment_asset_ledger f on a.equipment_id = f.equipment_asset_id left join work_ticket_type g on a.work_ticket_type_id = g.work_ticket_type_id left join standard_work_ticket h on a.standard_ticket_id = h.standard_ticket_id left join sp_code i on (a.flow_status = i.name and i.code_type_id = '1046') left join org_architecture j on a.work_team = j.architecture_id left join isc_user ua on a.maintainer = ua.user_id left join isc_user ub on a.applicant = ub.user_id left join isc_user uc on a.work_head = uc.user_id left join isc_user ud on a.signer = ud.user_id left join isc_user ue on a.run_main_person = ue.user_id left join isc_user uf on a.receive_head = uf.user_id left join isc_user ug on a.scene_licensor = ug.user_id left join isc_user uh on a.run_licensor = uh.user_id left join isc_user ui on a.head_change_to = ui.user_id left join isc_user uj on a.head_change_signer = uj.user_id left join isc_user uk on a.delay_head = uk.user_id left join isc_user ul on a.delay_licensor = ul.user_id left join isc_user um on a.work_end_head = um.user_id left join isc_user un on a.work_end_licensor = un.user_id left join isc_user uo on a.ticket_end_licensor = uo.user_id left join isc_user up on a.guardian = up.user_id left join isc_user uq on a.evaluation = uq.user_id where a.status = '01' and a.workorder_id is null and a.order_id is null
and a.work_ticket_type_id = #{map.workTicketTypeId}
and a.wind_id = #{map.startWindId}
and a.wind_id BETWEEN #{map.startWindId} and #{map.endWindId}
and a.crew_id = #{map.crewId}
and a.work_content like '%${map.workContent}%'
and a.apply_time between #{map.startTime}
and a.apply_time between #{map.startTime} and #{map.endTime}
order by a.serial_number is null desc,a.serial_number desc limit ${map.start},${map.pageSize}

select count(*) from work_ticket a left join org_architecture b on a.wind_id = b.architecture_code and b.architecture_type = '02' left join crew_information d on a.crew_id = d.crew_id left join position_encode e on a.position_id = e.position_id left join equipment_asset_ledger f on a.equipment_id = f.equipment_asset_id left join work_ticket_type g on a.work_ticket_type_id = g.work_ticket_type_id left join standard_work_ticket h on a.standard_ticket_id = h.standard_ticket_id left join sp_code i on (a.flow_status = i.name and i.code_type_id = '1046') left join org_architecture j on a.work_team = j.architecture_id left join isc_user ua on a.maintainer = ua.user_id left join isc_user ub on a.applicant = ub.user_id left join isc_user uc on a.work_head = uc.user_id left join isc_user ud on a.signer = ud.user_id left join isc_user ue on a.run_main_person = ue.user_id left join isc_user uf on a.receive_head = uf.user_id left join isc_user ug on a.scene_licensor = ug.user_id left join isc_user uh on a.run_licensor = uh.user_id left join isc_user ui on a.head_change_to = ui.user_id left join isc_user uj on a.head_change_signer = uj.user_id left join isc_user uk on a.delay_head = uk.user_id left join isc_user ul on a.delay_licensor = ul.user_id left join isc_user um on a.work_end_head = um.user_id left join isc_user un on a.work_end_licensor = un.user_id left join isc_user uo on a.ticket_end_licensor = uo.user_id left join isc_user up on a.guardian = up.user_id left join isc_user uq on a.evaluation = uq.user_id where a.status = '01' and a.workorder_id is null and a.order_id is null
and a.work_ticket_type_id = #{map.workTicketTypeId}
and a.wind_id = #{map.startWindId}
and a.wind_id BETWEEN #{map.startWindId} and #{map.endWindId}
and a.crew_id = #{map.crewId}
and a.work_content like '%${map.workContent}%'
and a.apply_time between #{map.startTime}
and a.apply_time between #{map.startTime} and #{map.endTime}

select a.*,(a.complete_count/a.apply_count) as 'complete_rate',b.architecture_name
from (select wind_id,count(*) as 'apply_count',
sum(IF(status = '01', 1, 0)) as 'in_process_count',
sum(IF(status = '02', 1, 0)) as 'complete_count',
sum(IF(status = '04', 1, 0)) as 'invalid_count'
from work_ticket
where 1=1

  and a.wind_id = #{startWindCode}


  and a.wind_id BETWEEN #{startWindCode} and #{endWindCode}


  and apply_time between #{startTime} and #{endTime}

group by wind_id) as a
left join org_architecture b on a.wind_id = b.architecture_code and b.architecture_type = '02'

select user_id from isc_user
and user_name = #{userName}
order by order_no
